<?php
class skin_abouts extends skin_pages{
	function loadDefault($currentItem){		global $bw, $vsLang, $vsTemplate, $vsFile;				$BWHTML .= <<<EOF			<div class="spmoi_all">			<if="$currentItem">				<div class="spm_title">{$currentItem->getTitle()}</div>				<div class="box_spm">					<div class="gioithieu">							<div class="content_pages">								{$currentItem->getContent()}							</div>					</div>				</div>			</if>			</div>			<div class="clear"></div>			<div class="qc3">				<if="$vsTemplate->global_template->partner">					<foreach="$vsTemplate->global_template->partner as $value">						<if="$value->getPosition()==5">							<a href="{$value->getWebsite()}" target="_blank">{$vsFile->arrayFiles[$value->getFileId()]->show(577,0,1,1,1,1)}</a>						</if>					</foreach>				</if>			</div>EOF;		return $BWHTML;	}}?>